<?php
/*
./php createphar.php test.phar 
*/

$p = new Phar($argv[1]);
$stubstring = ($argv[2]);
$p->startBuffering();

$p->setStub("<?php
\$b=\"{$stubstring}\";
echo \"\\nmounted successfully\\n\";
__HALT_COMPILER();
?>");

$p->addfile('getcontents.php');
$p->stopBuffering();
echo "created phar successfully\n";
?>

